mapper.select:
select e.*, u.name as teacher_name, qb.bank_name as bank_name 
from exam e left join user u on e.teacher_id = u.id 
left join question_bank qb on e.bank_id = qb.id


        e.exam_name like concat('%',#{examName},'%')


        and e.teacher_id = #{teacherId}


        and e.status = #{status}


        and e.start_time between #{startTime} and #{endTime}


order by e.update_time desc
